package Interface;import sound.GameSound;import javax.swing.*;import java.awt.event.MouseAdapter;import java.awt.event.MouseEvent;public class ClickEffect extends MouseAdapter {    private Menu menu;    private HighScoresPane highScoresPane;    private ControlPane controlPane;    public ClickEffect(Menu menu1){        menu = menu1;        highScoresPane = menu1.getHighScoresPane();        controlPane = menu1.getControlPane();    }    public void mouseEntered(MouseEvent e){        if(e.getSource() == menu.getLabelButtonPlay()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/Play_Button2.png"));            GameSound.getIstance().getAudio(GameSound.BOMB).play();            menu.getLabelButtonPlay().setIcon(iconPlay);        }        if(e.getSource() == menu.getLabelButtonHigh()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/highscoreButton2.png"));            GameSound.getIstance().getAudio(GameSound.BOMB).play();            menu.getLabelButtonHigh().setIcon(iconPlay);        }        if(e.getSource() == menu.getLabelButtonExit()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/exitButton2.png"));            GameSound.getIstance().getAudio(GameSound.BOMB).play();            menu.getLabelButtonExit().setIcon(iconPlay);        }        if(e.getSource() == menu.getHighScoresPane().getBackButton()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/backbutton2.png"));            menu.getHighScoresPane().getBackButton().setIcon(iconPlay);        }        if(e.getSource() == menu.getControlPane().getBackButton()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/backbutton2.png"));            menu.getControlPane().getBackButton().setIcon(iconPlay);        }        if(e.getSource() == menu.getLabelcontrolButton()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/controlButton2.png"));            menu.getLabelcontrolButton().setIcon(iconPlay);        }    }    public void mouseExited(MouseEvent e){        if(e.getSource() == menu.getLabelButtonPlay()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/Play_Button.png"));            menu.getLabelButtonPlay().setIcon(iconPlay);        }        if(e.getSource() == menu.getLabelButtonHigh()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/highscoreButton.png"));            menu.getLabelButtonHigh().setIcon(iconPlay);        }        if(e.getSource() == menu.getLabelButtonExit()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/exitButton.png"));            menu.getLabelButtonExit().setIcon(iconPlay);        }        if(e.getSource() == menu.getHighScoresPane().getBackButton()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/backbutton.png"));            menu.getHighScoresPane().getBackButton().setIcon(iconPlay);        }        if(e.getSource() == menu.getControlPane().getBackButton()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/backbutton.png"));            menu.getControlPane().getBackButton().setIcon(iconPlay);        }        if(e.getSource() == menu.getLabelcontrolButton()){            ImageIcon iconPlay = new ImageIcon(getClass().getResource("/Images/controlButton.png"));            menu.getLabelcontrolButton().setIcon(iconPlay);        }    }    public void mousePressed(MouseEvent e){        if(e.getSource() == menu.getLabelButtonPlay()){            menu.getCardLayout().show(menu.getMenu(),"GAME_PANEL");            menu.getGamePanel().startGame();            GameSound.getIstance().stop();            GameSound.getIstance().getAudio(GameSound.PLAYGAME).play();        }        if(e.getSource() == menu.getLabelButtonHigh()){            menu.getCardLayout().show(menu.getMenu(),"HIGH_SCORES");        }        if(e.getSource() == menu.getLabelButtonExit()){                int n = JOptionPane.showConfirmDialog(menu.getMenu(),"BAN CÓ MUỐN THOÁT?","",JOptionPane.YES_NO_OPTION,JOptionPane.QUESTION_MESSAGE);                if(n==0){                    menu.getGameScreen().dispose();                    System.exit(0);                }else{            }        }        if(e.getSource() == menu.getHighScoresPane().getBackButton()){            menu.getCardLayout().show(menu.getMenu(),"MENU");        }        if(e.getSource() == menu.getControlPane().getBackButton()){            menu.getCardLayout().show(menu.getMenu(),"MENU");        }        if(e.getSource() == menu.getLabelcontrolButton()){            menu.getCardLayout().show(menu.getMenu(),"CONTROL");        }    }}