package Interface;import javax.swing.*;import java.awt.*;import java.awt.event.MouseAdapter;public class ControlPane {        private JPanel panel;        private JLabel backgroundcontrol;        private JLabel background;        private JLabel backButton;        private JLabel menu;        public ControlPane( Menu menu1){            panel = new JPanel();            menu = new JLabel();            panel.setLayout(null);            backgroundcontrol = new JLabel();            background = new JLabel();            backButton = new JLabel();            background = setImageIcon(0,-15,"/Images/menu_background.png");            backgroundcontrol = setImageIcon(55,40,"/Images/background_option.png");            backButton = setImageIcon(827,564,"/Images/backbutton.png");            panel.add(backButton);            panel.add(backgroundcontrol);            panel.add(background);            setClick(new ClickEffect(menu1));        }        public JLabel setImageIcon(int x, int y, String source){            JLabel jLabel = new JLabel();            ImageIcon imageIcon = new ImageIcon(getClass().getResource(source));            jLabel.setBounds(x,y,imageIcon.getIconWidth(),imageIcon.getIconHeight());            jLabel.setIcon(imageIcon);            return jLabel;        }        public JPanel getPanel() {            return panel;        }        public void setClick(MouseAdapter click){            backButton.addMouseListener(click);        }        public JLabel getBackButton() {            return backButton;        }    public JLabel getBackgroundcontrol() {        return backgroundcontrol;    }}