package Interface;import Interface.GameScreen;public class Main {    public static void main(String []args){        GameScreen gameScreen = new GameScreen();        Menu menu = new Menu(gameScreen);        gameScreen.addKeyListener(menu.getGamePanel());        gameScreen.setVisible(true);        while(true) {            System.out.println("");            if(menu.getGamePanel().bomberMan.isDied())  {                System.out.println("goi");                menu.getGamePanel().getScores().saveScores();                menu.getGamePanel().getScores().getTopHighScores();                menu = new Menu(gameScreen);                gameScreen.addKeyListener(menu.getGamePanel());                gameScreen.setVisible(true);            }            if(menu.getGamePanel().iswin())  {                System.out.println("goi");                menu.getGamePanel().getScores().saveScores();                menu.getGamePanel().getScores().getTopHighScores();                menu = new Menu(gameScreen);                gameScreen.addKeyListener(menu.getGamePanel());                gameScreen.setVisible(true);            }        }    }}