package Interface;import sound.GameSound;import javax.swing.*;import java.awt.*;import java.awt.event.MouseAdapter;public class Menu {    private JPanel menu;    private CardLayout cardLayout;    private GameScreen gameScreen;    private JPanel view;    private JLabel labelBackground;    private JLabel labelButtonPlay;    private JLabel labelButtonHigh;    private JLabel labelButtonExit;    private GamePanel gamePanel;    private HighScoresPane highScoresPane;    private ControlPane controlPane;    private JLabel labelcontrolButton;    public Menu(GameScreen screen){        menu = new JPanel();        gameScreen = screen;        cardLayout = new CardLayout();        view = new JPanel();        view.setLayout(null);        menu.setLayout(cardLayout);        gamePanel = new GamePanel();        highScoresPane = new HighScoresPane(gamePanel.getScores(),this);        controlPane = new ControlPane(this);        setMenu();        menu.add(gamePanel,"GAME_PANEL");        menu.add(highScoresPane.getPanel(),"HIGH_SCORES");        menu.add(controlPane.getPanel(),"CONTROL");        gameScreen.add(menu);        GameSound.getIstance().stop();        GameSound.getIstance().getAudio(GameSound.MENU).play();        setClick(new ClickEffect(this));    }    public void setClick(MouseAdapter click){        labelButtonPlay.addMouseListener(click);        labelButtonExit.addMouseListener(click);        labelButtonHigh.addMouseListener(click);        labelcontrolButton.addMouseListener(click);    }    public void setMenu(){        labelBackground = setImageIcon(0,-15,"/Images/menu_background.png");        labelButtonPlay = setImageIcon(46,274,"/Images/Play_Button.png");        labelButtonHigh = setImageIcon(46,354,"/Images/highscoreButton.png");        labelButtonExit = setImageIcon(46,514,"/Images/exitButton.png");        labelcontrolButton = setImageIcon(46,434,"/Images/controlButton.png");        view.add(labelcontrolButton);        view.add(labelButtonPlay);        view.add(labelButtonHigh);        view.add(labelButtonExit);        view.add(labelBackground);        menu.add(view,"MENU");    }    public JLabel setImageIcon(int x, int y, String source){        JLabel jLabel = new JLabel();        ImageIcon imageIcon = new ImageIcon(getClass().getResource(source));        jLabel.setBounds(x,y,imageIcon.getIconWidth(),imageIcon.getIconHeight());        jLabel.setIcon(imageIcon);        return jLabel;    }    public JPanel getMenu() {        return menu;    }    public void setMenu(JPanel menu) {        this.menu = menu;    }    public CardLayout getCardLayout() {        return cardLayout;    }    public void setCardLayout(CardLayout cardLayout) {        this.cardLayout = cardLayout;    }    public GameScreen getGameScreen() {        return gameScreen;    }    public void setGameScreen(GameScreen gameScreen) {        this.gameScreen = gameScreen;    }    public JPanel getView() {        return view;    }    public void setView(JPanel view) {        this.view = view;    }    public JLabel getLabelBackground() {        return labelBackground;    }    public void setLabelBackground(JLabel labelBackground) {        this.labelBackground = labelBackground;    }    public JLabel getLabelButtonPlay() {        return labelButtonPlay;    }    public void setLabelButtonPlay(JLabel labelButtonPlay) {        this.labelButtonPlay = labelButtonPlay;    }    public JLabel getLabelButtonHigh() {        return labelButtonHigh;    }    public JLabel getLabelButtonExit() {        return labelButtonExit;    }    public GamePanel getGamePanel() {        return gamePanel;    }    public void setGamePanel(GamePanel gamePanel) {        this.gamePanel = gamePanel;    }    public HighScoresPane getHighScoresPane() {        return highScoresPane;    }    public ControlPane getControlPane() {        return controlPane;    }    public JLabel getLabelcontrolButton() {        return labelcontrolButton;    }}